seq1="*"
seq2="*"
time ./gsa-go --seq1 $seq1 --seq2 $seq2 --seqtype protein --gap 12 --gapext 2

echo "\nRunning GSA on same sequences with different gap and extention penalties ...\n"

seq1="*"
seq2="*"
time ./gsa-go --seq1 $seq1 --seq2 $seq2 --seqtype protein --gap 12 --gapext 2 --outtype onlyscore
